import React, {Component} from 'react';
import {StyleSheet, View, Text, TextInput, Switch} from 'react-native';
import {Blurhash} from 'react-native-blurhash';

export default class App extends Component {
  state = {
    blurhash: 'LGFFaXYk^6#M@-5c,1J5@[or[Q6.',
    decodeAsync: true,
  };
  componentDidMount() {}

  render() {
    return (
      <View style={styles.container}>
        <Blurhash
          blurhash={this.state.blurhash}
          decodeWidth={32}
          decodeHeight={32}
          decodePunch={1}
          decodeAsync={this.state.decodeAsync}
          style={styles.blurhashImage}
          resizeMode="cover"
        />
        <TextInput
          value={this.state.blurhash}
          placeholder="Blurhash"
          onChangeText={(text) => {
            this.setState({
              blurhash: text,
            });
          }}
          style={styles.blurhashTextInput}
        />
        {/* To test if `decodeAsync` really doesn't block the UI thread, you can press this Touchable and see it reacting. */}
        <View style={styles.row}>
          <Text style={styles.text}>Decode Async:</Text>
          <Switch
            value={this.state.decodeAsync}
            onValueChange={(v) => this.setState({decodeAsync: v})}
          />
        </View>
      </View>
    );
  }
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#F5FCFF',
  },
  blurhashImage: {
    width: 300,
    height: 200,
    // Custom styling for width, height, scaling etc here
  },
  blurhashTextInput: {
    marginTop: 20,
    borderRadius: 5,
    borderWidth: 1,
    borderColor: 'purple',
    width: '70%',
    height: 35,
    paddingHorizontal: 20,
    textAlign: 'center',
  },
  row: {
    marginTop: 30,
    flexDirection: 'row',
    alignItems: 'center',
  },
  text: {
    fontSize: 14,
    marginBottom: 3,
  },
});
